INSERT INTO `wp_quotescollection` (`quote_id`, `quote`, `author`, `source`, `tags`, `public`, `time_added`, `time_updated`) VALUES
(1, 'You put two things together and it becomes something else. If you put them together in the right way, it generates a totally new experience that can be very powerful.', '', '', '', 'yes', '2017-08-19 22:52:05', NULL),
(2, 'One “dreams” into a painting. Not in the of Dali, but this inexplicable feeling that one has, and sudden, spur of the moment decisions are very important', '', '', '', 'yes', '2017-08-19 22:52:20', NULL),
(3, 'There is no such thing as nonsense.', '', '', '', 'yes', '2017-08-19 22:52:25', NULL),
(4, 'Content is important as in the complexity of image and ideas, relationships format and sources.', '', '', '', 'yes', '2017-08-19 22:52:38', NULL),
(5, 'There needs to be a game which is constantly played with the painting which is never resolved. It’s better that way when the painting is self generative.', '', '', '', 'yes', '2017-08-19 22:52:45', NULL),
(6, 'I am constantly reaffirming everything I’ve done, but at the same time constantly destroying, ending that, and starting a new thing.', '', '', '', 'yes', '2017-08-19 22:52:58', NULL),
(7, 'Each painting in its break-down and build-up becomes a personal experience.', '', '', '', 'yes', '2017-08-19 22:53:05', NULL);
